import React from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import CardGroup from 'react-bootstrap/CardGroup';
import CardColumns from 'react-bootstrap/CardColumns';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFileCode, faWindowMaximize } from "@fortawesome/free-solid-svg-icons";
import "./style.css";

const Thumbnail = (props) => {
    return (

                        <Card bg="dark" border="dark" text="white"
                        // style={{ width: '18rem' }}
                        >
                            <Card.Img
                                variant="top"
                                src={props.img} />
                            <Card.Body>
                                <Card.Title>{props.title}</Card.Title>
                                <Card.Text>
                                    {props.summary}
                                </Card.Text>
                                {/* <Button variant="outline-light"> Learn More</Button> */}
                                {/* <div className="icon">
                            <a href={props.code}><FontAwesomeIcon icon={faFileCode} id="linked"/></a>
                            <a href={props.link}><FontAwesomeIcon icon={faWindowMaximize} id="linked"/></a>
                        </div> */}
                            </Card.Body>
                        </Card>
    )
}

export default Thumbnail;
