import React from "react";
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import CardGroup from 'react-bootstrap/CardGroup';
import CardColumns from 'react-bootstrap/CardColumns';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFileCode, faWindowMaximize } from "@fortawesome/free-solid-svg-icons";
import "./style.css";

const Thumbnail = (props) => {
    return (
        <CardGroup>
            <CardColumns>
                <Card bg="dark" border="dark" text="white" 
                // style={{ width: '18rem' }}
                >
                    <Card.Img 
                    variant="top" 
                    src={props.img} />
                    <Card.Body>
                        <Card.Title>{props.title}</Card.Title>
                        <Card.Text>
                            {props.summary}
                        </Card.Text>
                        {/* <Button variant="outline-light"> Learn More</Button> */}
                        <div className="icon">

                        <FontAwesomeIcon href={props.code} icon={faFileCode}/>
                        <FontAwesomeIcon href={props.link} icon={faWindowMaximize}/>
                        </div>
                    </Card.Body>
                </Card>
            </CardColumns>
        </CardGroup>
    )
}

export default Thumbnail;
